mapper.select:
select m.id, m.status, m.content, m.froms, m.uuid, m.create_user_id, date_format(m.create_time,'%Y-%m-%d %H:%i:%s') create_time, m.has_img, m.can_comment, m.can_transmit,
	m.read_number, m.location, m.longitude, m.latitude, m.zan_number, m.comment_number, m.transmit_number, m.share_number, u.account
from t_mood m inner join t_user u on u.id = m.create_user_id 
where 


        (m.status = #{status_normal} or m.status = #{status_self}) 


        m.status = #{status_normal} 


and m.create_user_id = #{to_user_id}
order by m.id desc limit #{start},#{page_size}
