mapper.select:
select v.id, v.create_user_id, date_format(v.create_time,'%Y-%c-%d %H:%i:%s') create_time
from t_visitor v 
where v.status=#{status} and v.user_id = #{user_id} and v.table_name=#{table_name} and v.table_id = #{table_id} 
GROUP BY v.create_user_id
order by v.create_time desc, v.id desc limit #{start}, #{page_size}
