mapper.select:
select s.id, s.status, s.create_user_id, date_format(s.create_time,'%Y-%m-%d %H:%i:%s') create_time, s.seat_id, s.seat_name, s.platform, s.user_id, date_format(s.allot_time,'%Y-%m-%d %H:%i:%s') allot_time, s.modify_user_id, date_format(s.modify_time,'%Y-%m-%d %H:%i:%s') modify_time from t_mall_promotion_seat s
where (s.platform = #{platform}
order by s.id desc limit #{start}, #{pageSize}
