/**
**主题模板的渲染
*/
function render(a){a&&(isEmpty(a.title)||$("#paper-title").html(a.title),isEmpty(a.call)||$("#paper-call").html(a.call),isEmpty(a.body)||$("#paper-body").html(a.body),isEmpty(a.greeting)||$("#paper-signature-greeting").html(a.greeting),isEmpty(a.signatureName)||$("#paper-signature-name").html(a.signatureName),isEmpty(a.signatureTime)||$("#paper-signature-time").html(a.signatureTime))};